import React, { useState, useEffect } from "react";
import { Button, Table, Container, Row, Col } from "react-bootstrap";
import { withRouter } from "react-router";
import { useParams } from "react-router-dom";
import { getWorkout } from "../../api";
import { Link } from "react-router-dom";



const WorkoutView = (props) => {
  const params = useParams();
//   console.log("params", params);
//   console.log("prrrr: ", props);


  const [added, setAdded] = useState(false);
  const [clicked, setClicked] = useState(false);
  const [workout, setWorkout] = useState({});
  const [exercises, setExercises] = useState([])
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getWorkout(params.id).then((data) => {
      setLoading(false)
      setWorkout(data.name);
      setExercises(data.exercises)
      console.log("individ workout", data);
    });
  }, []);

  const handleOnClick = () => {
    setAdded(true);
    setClicked(true);
  };

  console.log("state..... ", added);

  return (
    <>
    <Container>

    {loading && <p>Loading</p>}
    {!loading && 
      <div>
      <div>
        <p>{workout.name}</p>
        <Table striped bordered hover size="sm" variant="dark">
  <thead>
    <tr>
      <th>Exercise</th>
      <th>Description</th>
      <th>Muscle Type</th>
    </tr>
  </thead>
  <tbody>
  {exercises.map((exercise) => {
    return(
    <tr key={exercise.id}>
      <td>{exercise.name}</td>
      <td>{exercise.description}</td>
      <td>{exercise.muscleType}</td>
    </tr>)})}
  </tbody>
</Table>
      </div>
      {added ? <h3>Workout added</h3> : null}
      <Button onClick={handleOnClick}size="lg" variant="success">Add to saved workouts</Button>
    </div>

              }
                    <div>
        <Link to={`/workouts/saved`}>
          <Button variant="primary" size="lg" active>
            Add new exercise
          </Button>
        </Link>
      </div>

    </Container>
    </>
  );
};

export default withRouter(WorkoutView);
