import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import { withRouter } from "react-router";

const workoutsData = [
  {
    id: 1,
    name: "workout 1",
    muscleGroup: "glutes",
    isSaved: false,
  },
  {
    id: 2,
    name: "workout 2",
    muscleGroup: "arms",
    isSaved: false,
  },
  {
    id: 3,
    name: "workout 3",
    muscleGroup: "back",
    isSaved: false,
  },
];
const WorkoutsView = (props) => {
  console.log(props);

  const handleRemoveButtonOnClick = (workoutId) => {
    console.log("handleRemoveButtonOnClick:::: ", workoutId);
  };

  const handleSaveButtonOnClick = (workoutId) => {
    console.log("handleSaveButtonOnClick:::: ", workoutId);
  };

  return (
    <div>
      <h1>all workouts page</h1>
      <div>
               <Button href={`/workouts/add`}>
                        New Workout
                </Button> 
           </div>
      <table>
        <thead>
          <tr>
            <th>name</th>
            <th>Muscle group</th>
          </tr>
        </thead>
        <tbody>
          {workoutsData.map((workout) => {
            return (
              <tr key={workout.id}>
                <td>
                  <Button href={`/workouts/view/${workout.id}`}>
                    {workout.name}
                  </Button>
                </td>
                <td>{workout.muscleGroup}</td>
                <td>
                  {workout.isSaved ? (
                    <Button variant="danger" onClick={() => handleRemoveButtonOnClick(workout.id)}>
                      Remove
                    </Button>
                  ) : (
                    <Button variant="success" onClick={() => handleSaveButtonOnClick(workout.id)}>
                      Save
                    </Button>
                  )}
                </td>
              </tr>


            );
          })}
        </tbody>
      </table>
    </div>

  );
};

export default withRouter(WorkoutsView);
