import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import {updateProfile} from "../../api";
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
import SaveIcon from '@material-ui/icons/Save';



const EditProfileView = () => {
  const handleSubmit = (event) => {
    event.preventDefault();
    updateProfileHandler();
    console.log('submit click')
  }

  const [submitMsg, setSubmitMsg] = useState({msg: "", state: false});
  const [name, setName] = useState();
  const [age, setAge] = useState();
  const [weight, setWeight] = useState();
  const [height, setHeight] = useState();



  const updateProfileHandler = async () => {
    const profileDetails = {name: name, age: age, weight: weight, height: height}
    try {
      const data = await updateProfile(profileDetails);
      console.log("clicked subimt profile", data);
      setSubmitMsg({msg: "Profile Updated", state: true});
    }
    catch(e) {
      console.log(e);
      setSubmitMsg({msg: "Something went wrong, please try again!", state: false});
    } 
  }

  const changeHandler = (e) => {
    if (e.target.name == "name") {
      setName(e.target.value);
    } else if (e.target.name == "age") {
      setAge(e.target.value);
    } else if (e.target.name == "height") {
      setHeight(e.target.value);
    } else {
      setWeight(e.target.value);
    } 
  };

  const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
  }));

  const classes = useStyles();


  return ( 
    <div>
      <h2>Edit profile page</h2>
      <form onSubmit={handleSubmit} className={classes.root} noValidate autoComplete="off">

        <TextField id="standard-basic" label="Name">
          <input
            type="text"
            name="name"
            onChange={changeHandler}
            value={name}
          />
          </TextField>


          <TextField id="standard-basic" label="Age">
          <input
            type="number"
            name="age"
            onChange={changeHandler}
            value={age}
          />
          </TextField>

          <TextField id="standard-basic" label="Height">
          <input
            type="number"
            name="height"
            onChange={changeHandler}
            value={height}
          />
          </TextField>

          <TextField id="standard-basic" label="Weight">
          <input
            type="number"
            name="weight"
            onChange={changeHandler}
            value={weight}
          />
          </TextField>

          <Button
        variant="contained"
        color="primary"
        size="large"
        type="submit"
        value="Submit"
        className={classes.button}
        startIcon={<SaveIcon />}
      >
        Save
      </Button>

      </form>
    </div>
  );
};

export default EditProfileView;
