import React, { useState, useEffect } from "react";
import { getExercises, addWorkout } from "../../api";
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";


const AddWorkoutsView = () => {
  const handleSubmit = (event) => {
    event.preventDefault();
    addWorkoutHandler();
    console.log("submit click");
  };
  const [exercises, setExercises] = useState([]);

  useEffect(() => {
    refreshExercises();
  }, []);

  const refreshExercises = async () => {
    const data = await getExercises();
    setExercises(data);
  };

  const [added, setAdded] = useState(false);


  const addWorkoutHandler = async (workoutDetails) => {
    try {
      const data = await addWorkout(workoutDetails);
      console.log("clicked submitadd ex", data);
      setSubmitMsg({msg: "Workout added", state: true});
    }
    catch(e) {
      console.log(e);
      setSubmitMsg({msg: "Something went wrong, please try again!", state: false});
    } 
  }


  const handleOnClick = () => {
    setAdded(true);
  };

  return (
    <div>
      <h2>Add workout page</h2>
      <div>
        <Link to={`/exercises/add`}>
          <Button>New Exercise</Button>
        </Link>
      </div>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            // onChange={(e) => setTitle(e.currentTarget.value)}
            // value={title}
          />
        </label>

        <input type="submit" value="Submit" />
      </form>

      {exercises.map((exercise) => {
        return (
          <>
            <li>{exercise.name}</li>
            <Button onClick={handleOnClick}>Add to saved workouts</Button>
            {added ? <h3> Exercise added</h3> : null}
          </>
        );
      })}
    </div>
  );
};

export default AddWorkoutsView;
