import React, { useState } from "react";
import { addExercise } from "../../api";
import FormCompleteMsg from "../FormCompleteMsg";
import { Link } from "react-router-dom";
import { Button, Table } from "react-bootstrap";

const AddExercisesView = (props) => {
  const [name, setName] = useState();
  const [description, setDescription] = useState();
  const [muscleType, setMuscleType] = useState();

  const [submitMsg, setSubmitMsg] = useState({ msg: "", state: false });
  const [redirectHome, setRedirectHome] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    addExerciseHandler();

    setName(""); //blank the form
    setDescription("");
  };

  const addExerciseHandler = async (exerciseDetails) => {
    console.log("ex details", exerciseDetails);

    try {
      const data = await addExercise(exerciseDetails);
      setSubmitMsg({ msg: "Exercise Added, add another?", state: true });
    } catch (e) {
      console.log(e);
      setSubmitMsg({
        msg: "Something went wrong, please try again!",
        state: false,
      });
    }
  };

 const changeHandler = (e) => {
    if (e.target.name == "name") {
      setName(e.target.value);
    } else if (e.target.name == "muscleType") {
      setMuscleType(e.target.value);
    } else {
      setDescription(e.target.value);
    } 
  };

  return (
    <div>
      add exercise page
      <div>
        <form onSubmit={handleSubmit}>
          <label>
            Name:
            <input type="text" name="name" onChange={changeHandler} value={name} />
          </label>

          <label>
            Description:
            <input type="text" name="description" onChange={changeHandler} value={description}/>
          </label>


          <label>
            Muscle Type:
            <input type="text" name="muscleType" onChange={changeHandler} value={muscleType}/>
          </label>

          <input type="submit" value="Submit" />
        </form>
      </div>
    </div>
  );
};

export default AddExercisesView;
