import React, { useState, useEffect } from "react";
import { getExercises, addWorkout, getWorkouts, addExerciseToWorkout } from "../../api";
import { Button, Table, Form } from "react-bootstrap";
import { Link } from "react-router-dom";

const AddWorkoutsView = () => {
  const handleSubmit = (event) => {
    event.preventDefault();
    addWorkoutHandler();
    console.log("submit click");
  };
  const [exercises, setExercises] = useState([]);
  const [workoutsData, setWorkoutsData] = useState([]);

  useEffect(() => {
    refreshExercises();
    getWorkouts().then(data => {
      setWorkoutsData(data);
    });
  }, []);

  const refreshExercises = async () => {
    const data = await getExercises();
    setExercises(data);
  };

  const [added, setAdded] = useState(false);
  const [submitMsg, setSubmitMsg] = useState({ msg: "", state: false });
  const [workoutSelected, setWorkoutSelected] = useState("");


  const addWorkoutHandler = async (workoutDetails) => {
    try {
      const data = await addWorkout(workoutDetails);
      console.log("clicked submitadd ex", data);
      setSubmitMsg({ msg: "Workout added", state: true });
    } catch (e) {
      console.log(e);
      setSubmitMsg({
        msg: "Something went wrong, please try again!",
        state: false,
      });
    }
  };



  //exercise button
  const handleOnClick = (e) => {
    const payload = {workoutID: workoutSelected, exerciseID: e.target.value }
    addExerciseToWorkout(payload);
    setAdded(true);
    console.log('handle on click', e.target.value)
  };


//workout button
const handleWorkoutsChange = (e) => {
  console.log(e.target.value)
  setWorkoutSelected(e.target.value);
}

  return (
    <div>
      <h2>Add workout page</h2>
      <div>
        <Link to={`/exercises/add`}>
          <Button>New Exercise</Button>
        </Link>
      </div>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            // onChange={(e) => setTitle(e.currentTarget.value)}
            // value={title}
          />
        </label>

        <input type="submit" value="Submit" />
      </form>
      <>
        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              <th>Exercise</th>
              <th>Description</th>
              <th>Muscle Type</th>
            </tr>
          </thead>
          <tbody>
            {exercises.map((exercise) => {
              return (
                <tr key={exercise._id}>
                  <td>{exercise.name}</td>
                  <td>{exercise.description}</td>
                  <td>{exercise.muscleType}</td>
                  <td>
                  <Form.Control as="select" custom onChange={handleWorkoutsChange}>
                    {workoutsData.map((workout)=>{
                      return <option value={workout._id}>{workout.name}</option>
                    })}
                    </Form.Control>
                    <Button onClick={handleOnClick} value={exercise._id}>Add to workout</Button>
                    {added ? <h3> Exercise added</h3> : null}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </>
      );
    </div>
  );
};

export default AddWorkoutsView;
