import React, {useState} from 'react';
import Button from 'react-bootstrap/Button';
import { withRouter } from "react-router";

const workoutData = {
    id: 333,
    muscleGroup: 'glutes',
    name: 'workout 1',
}

const WorkoutView = (props) => {
    console.log('prrrr: ', props);
    // const workoutId = props.match.params.id;
    const workoutId = workoutData.id;

    const [added, setAdded] = useState(false);
    const [clicked, setClicked] = useState (false);


    const handleOnClick = () => {
        setAdded(true);
        setClicked (true)
    };

    console.log('state..... ', added);

    return (
        <div>
            <div>
            <Button href="/workouts/saved" variant="primary" size="lg" active>
          Add new exercise 
        </Button>
            </div>
            <div>
                <h1>individual workout view</h1>
                <h3>id: {workoutId}</h3>
                <h3>muscle group: glutes</h3>
            </div>

            {added ? <h3>Workout added</h3> : null}
            { clicked ? <Button onClick={handleOnClick}>Add to saved workouts</Button> : null }
        </div>
    )
  }

export default withRouter(WorkoutView);
