    });

    setTitle(""); //blank the form
    setDescription("");
  };


  return (
    <div>
      add exercise page
      <div>
      <form onSubmit={handleSubmit}>
          <label>
            Name:
            <input
              type="text"
              name="title"
              onChange={(e) => setTitle(e.currentTarget.value)}
            />
          </label>

          <label>
            Description:
            <input
              type="text"
              name="description"
              onChange={(e) => setDescription(e.currentTarget.value)}
            />
          </label>

          <input type="submit" value="Submit" />
        </form>
      </div>
    </div>
  );
};

export default AddExercisesView;
