import React, { useState, useEffect } from "react";
import {
  getExercises,
  addWorkout,
  getWorkouts,
  addExerciseToWorkout,
} from "../../api";
import { Table, Form, Row, Col } from "react-bootstrap";
import { useHistory, useLocation } from "react-router-dom";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Link from "@material-ui/core/Link";
import { makeStyles } from "@material-ui/core/styles";
import SaveIcon from "@material-ui/icons/Save";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import "fontsource-roboto";

const AddWorkoutsView = () => {
  const handleSubmit = (event) => {
    event.preventDefault();
    addWorkoutHandler();
    console.log("submit click");
  };
  const [exercises, setExercises] = useState([]);
  const [workoutsData, setWorkoutsData] = useState([]);

  useEffect(() => {
    refreshExercises();
    getWorkouts().then((data) => {
      setWorkoutsData(data);
    });
  }, []);

  const refreshExercises = async () => {
    const data = await getExercises();
    setExercises(data);
  };

  const [added, setAdded] = useState(false);
  const [submitMsg, setSubmitMsg] = useState({ msg: "", state: false });
  const [workoutSelected, setWorkoutSelected] = useState("");
  const [name, setName] = useState();

  const addWorkoutHandler = async () => {
    const workoutName = { name: name, bookmark: false };
    console.log("workout name", workoutName);
    try {
      const data = await addWorkout(workoutName);
      setSubmitMsg({ msg: "Workout added", state: true });
    } catch (e) {
      console.log(e);
      setSubmitMsg({
        msg: "Something went wrong, please try again!",
        state: false,
      });
    }
  };

  //exercise button
  const handleOnClick = (e) => {
    const payload = { workoutID: workoutSelected, exerciseID: e.target.value };
    addExerciseToWorkout(payload);
    setAdded(true);
    console.log("handle on click", e.target.value);
  };

  //workout button
  const handleWorkoutsChange = (e) => {
    console.log(e.target.value);
    setWorkoutSelected(e.target.value);
  };

  //new workout for
  const changeHandler = (e) => {
    if (e.target.name == "name") {
      setName(e.target.value);
    }
  };

  const useStyles = makeStyles((theme) => ({
    root: {
      "& > *": {
        margin: theme.spacing(1),
        width: "25ch",
      },
    },
  }));

  const classes = useStyles();

  return (
    <>
      <Container fixed>
        <div>
          <h2>Add workout page</h2>
        </div>
        <form
          onSubmit={handleSubmit}
          className={classes.root}
          noValidate
          autoComplete="off"
        >
          New workout name:
          <TextField
            type="text"
            name="name"
            id="standard-basic"
            label="Name"
            onChange={changeHandler}
            value={name}
          />
          <Button
            variant="contained"
            color="primary"
            size="large"
            type="submit"
            value="Submit"
            className={classes.button}
            startIcon={<SaveIcon />}
          >
            Save
          </Button>
        </form>
      </Container>
      <Container>
        <Link to={`/exercises/add`}>
          <Button variant="success">New Exercise</Button>
        </Link>
      </Container>

      <Container>
        <Row>
          <Col>
            <Table striped bordered hover size="sm" variant="dark">
              <thead>
                <tr>
                  <th>Exercise</th>
                  <th>Description</th>
                  <th>Muscle Type</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {exercises.map((exercise) => {
                  return (
                    <tr key={exercise._id}>
                      <td>{exercise.name}</td>
                      <td>{exercise.description}</td>
                      <td>{exercise.muscleType}</td>
                      <td>
                        <Form.Control
                          as="select"
                          custom
                          onChange={handleWorkoutsChange}
                        >
                          <option>Select workout</option>
                          {workoutsData.map((workout) => {
                            return (
                              <option value={workout._id}>
                                {workout.name}
                              </option>
                            );
                          })}
                        </Form.Control>
                        <Button
                          onClick={handleOnClick}
                          value={exercise._id}
                          variant="info"
                        >
                          Add to workout
                        </Button>
                        {added ? <h3> Exercise added</h3> : null}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </Table>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default AddWorkoutsView;
