import React, { useState, useEffect } from "react";
import { Table, Container, Row, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import { getProfile } from "../../api";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Box from '@material-ui/core/Box';
import Avatar from '@material-ui/core/Avatar';
import Divider from '@material-ui/core/Divider';
import { useFadedShadowStyles } from '@mui-treasury/styles/shadow/faded';
import { useGutterBorderedGridStyles } from '@mui-treasury/styles/grid/gutterBordered';

const ProfileView = () => {
  const [profile, setProfile] = useState({});

  useEffect(() => {
    refreshProfile();
  }, []);

  const refreshProfile = async () => {
    const data = await getProfile();
    setProfile(data);
  };
  const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });
  const classes = useStyles();

  return (
    <>

<Card className={cx(styles.card, shadowStyles.root)}>
      <CardContent key={profile._id}>
        <Avatar className={styles.avatar} src={'https://i.pravatar.cc/300'} />
        <h3 className={styles.heading}>{profile.name}</h3>
      </CardContent>
      <Divider light />
      <Box display={'flex'}>
        <Box p={2} flex={'auto'} className={borderedGridStyles.item}>
          <p className={styles.statLabel}>Followers</p>
          <p className={styles.statValue}>6941</p>
        </Box>
        <Box p={2} flex={'auto'} className={borderedGridStyles.item}>
          <p className={styles.statLabel}>age: {profile.age}</p>
          <p className={styles.statLabel}>weight: {profile.weight} kg</p>
          <p className={styles.statLabel}>height: {profile.height} cm</p>

        </Box>
      </Box>
    </Card>


        <Container>
        <Row>
          <Col>
            <div>
              <h1>Profile</h1>
            </div>
            <Table striped bordered hover size="sm" variant="dark">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Weight</th>
                  <th>Height</th>
                </tr>
              </thead>
              <tbody>
                <tr key={profile._id}>
                  <td>{profile.name}</td>
                  <td>{profile.age} </td>
                  <td>{profile.weight} kg</td>
                  <td>{profile.height} cm</td>
                </tr>
              </tbody>
            </Table>
          </Col>
        </Row>
      </Container>

      <Container fluid="md">
        <Row>
          <Col>
            <Link to={"/workouts/saved"}>
              <Button variant="info" size="lg" active>
                Saved workouts
              </Button>
            </Link>
          </Col>
          <Col>
            <Link to={"/workouts/add"}>
              <Button variant="info" size="lg" active>
                Add new workouts
              </Button>
            </Link>
          </Col>
          <Col>
            <Link to={"/profile/edit"}>
              <Button variant="info" size="lg" active>
                Edit profile
              </Button>
            </Link>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default ProfileView;
