import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import {updateProfile} from "../../api";

const EditProfileView = () => {
  const handleSubmit = (event) => {
    event.preventDefault();
    updateProfileHandler();
    console.log('submit click')
  }

  const [submitMsg, setSubmitMsg] = useState({msg: "", state: false});

  const updateProfileHandler = async (profileDetails) => {
    try {
      const data = await updateProfile(profileDetails);
      console.log("clicked subimt profile", data);
      setSubmitMsg({msg: "Profile Updated", state: true});
    }
    catch(e) {
      console.log(e);
      setSubmitMsg({msg: "Something went wrong, please try again!", state: false});
    } 
  }

  return ( 
    <div>
      <h2>Edit profile page</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            // onChange={(e) => setTitle(e.currentTarget.value)}
            // value={title}
          />
        </label>

        <label>
          Age:
          <input
            type="number"
            name="age"
            // onChange={(e) => setTitle(e.currentTarget.value)}
            // value={title}
          />
        </label>

        <label>
          Height:
          <input
            type="text"
            name="height"
            // onChange={(e) => setTitle(e.currentTarget.value)}
            // value={title}
          />
        </label>

        <label>
          Weight:
          <input
            type="text"
            name="weight"
            // onChange={(e) => setTitle(e.currentTarget.value)}
            // value={title}
          />
        </label>

        <input type="submit" value="Submit" />
      </form>
    </div>
  );
};

export default EditProfileView;
