        <Link to={`/workouts/saved`}>
          <Button variant="primary" size="lg" active>
            Add new exercise
          </Button>
        </Link>
      </div>
      <div>
        <p>{workout.name}</p>
        <Table striped bordered hover size="sm">
  <thead>
    <tr>
      <th>Exercise</th>
      <th>Description</th>
      <th>Muscle Type</th>
    </tr>
  </thead>
  <tbody>
  {exercises.map((exercise) => {
    return(
    <tr key={exercise.id}>
      <td>{exercise.name}</td>
      <td>{exercise.descritpion}</td>
      <td>{exercise.muscleType}</td>
    </tr>)})}
  </tbody>
</Table>

      <h1>individual workout view</h1>
       <h3>muscle group: glutes</h3> 
      </div>

      {added ? <h3>Workout added</h3> : null}
      <Button onClick={handleOnClick}>Add to saved workouts</Button>
    </div>
