    //     <p>{workout.name}</p>
        {/* <p>{workout.exercises[0]}</p> */}
        {/* <ul>
                {workout.exercises.map((singleWorkout) => {
                    return (
                   <li>{singleWorkout.name}</li>
                    )
                })}
                </ul> */}

        {/* <h1>individual workout view</h1>
