import React, { useState } from "react";
import {addExercise} from "../../api";
import FormCompleteMsg from './FormCompleteMsg';



const AddExercisesView = (props) => {
  const [title, setTitle] = useState(props.exercise.title);
  const [description, setDescription] = useState(props.exercise.description);

  const [submitMsg, setSubmitMsg] = useState({msg: "", state: false});
  const [redirectHome, setRedirectHome] = useState(false);


  const handleSubmit = (event) => {
    event.preventDefault();

    //call the add movie function in the parent class
    props.submitHandler({
      // title: title,
      // description: description
    });

    setTitle(""); //blank the form
    setDescription("");
  };


  const addExerciseHandler = async (exerciseDetails) => {
    try {
      const data = await addExercise(exerciseDetails);
      console.log(data);
      setSubmitMsg({msg: "Exercise Added, add another?", state: true});
    }
    catch(e) {
      console.log(e);
      setSubmitMsg({msg: "Something went wrong, please try again!", state: false});
    } 
  }


  return (
    <div>
      add exercise page
      <div>
      <form onSubmit={handleSubmit} submitHandler={addExerciseHandler}>
          <label>
            Name:
            <input
              type="text"
              name="title"
            />
          </label>

          <label>
            Description:
            <input
              type="text"
              name="description"
            />
          </label>

          <input type="submit" value="Submit" />
        </form>
      </div>
    </div>
  );
};

export default AddExercisesView;
