import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import { withRouter } from "react-router";
import { useParams } from "react-router-dom";
import { getWorkout } from "../../api";

const WorkoutView = (props) => {
  const params = useParams();
  console.log("params", params);

  console.log("prrrr: ", props);
  // const workoutId = props.match.params.id;
  // const workoutId = workoutData.id;

  const [added, setAdded] = useState(false);
  const [clicked, setClicked] = useState(false);
  const [workout, setWorkout] = useState([]);

  useEffect(() => {
    getWorkout(params.id).then((data) => {
      setWorkout(data);
      console.log("individ workout", data.exercises[0]);
    });
  }, []);

  const handleOnClick = () => {
    setAdded(true);
    setClicked(true);
  };

  console.log("state..... ", added);

  return (
    <div>
      <div>
        <Link to={`/workouts/saved`}>
          <Button variant="primary" size="lg" active>
            Add new exercise
          </Button>
        </Link>
      </div>
      <div>
        <p>{workout.name}</p>
        {/* <p>{workout.exercises[0]}</p> */}
        {/* <ul>
                {workout.exercises.map((singleWorkout) => {
                    return (
                   <li>{singleWorkout.name}</li>
                    )
                })}
                </ul> */}

        {/* <h1>individual workout view</h1>
                {/* <h3>id: {workoutId}</h3> */}
        {/* <h3>muscle group: glutes</h3> */}
      </div>

      {added ? <h3>Workout added</h3> : null}
      <Button onClick={handleOnClick}>Add to saved workouts</Button>
    </div>
  );
};

export default withRouter(WorkoutView);
