import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import { withRouter } from "react-router";

const workoutsData = [
  {
    id: 1,
    name: "workout 1",
    muscleGroup: "glutes",
    isSaved: true,
  },
  {
    id: 2,
    name: "workout 2",
    muscleGroup: "arms",
    isSaved: false,
  },
  {
    id: 3,
    name: "workout 3",
    muscleGroup: "back",
    isSaved: false,
  },
];
const WorkoutsView = (props) => {
  console.log(props);

  const handleOnClick = () => {
    console.log("handleOnClick::::");
  };

  return (
    <div>
      <h1>all workouts page</h1>
      <table>
        <thead>
          <tr>
            <th>name</th>
            <th>Muscle group</th>
          </tr>
        </thead>
        <tbody>
          {workoutsData.map((workout) => {
            return (
              <tr key={workout.id}>
                <td>
                  <Button href={`/workouts/${workout.id}`}>
                    {workout.name}
                  </Button>
                </td>
                <td>{workout.muscleGroup}</td>
                <td>
                  {workout.isSaved ? (
                    <Button variant="danger" onClick={handleOnClick}>
                      Remove
                    </Button>
                  ) : (
                    <Button variant="success" onClick={handleOnClick}>
                      Save
                    </Button>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default withRouter(WorkoutsView);
